dir = (0, 1)
alpha = 'ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz'
letters = []

def dch(dir):
  if dir[0] == 0: return '|'
  else: return '-'

def dt(pos, dir):
  dp = (dir[1], dir[0])
  dn = (-dir[1], -dir[0])
  pp = (pos[0] + dp[0], pos[1] + dp[1])
  pn = (pos[0] + dn[0], pos[1] + dn[1])
  if len(field) > pp[1] and pp[1] > 0 and \
    len(field[0]) > pp[0] and pp[0] > 0 and \
    field[pp[1]][pp[0]] != ' ': return dp
  if len(field) > pn[1] and pn[1] > 0 and \
    len(field[0]) > pn[0] and pn[0] > 0 and \
    field[pn[1]][pn[0]] != ' ': return dn
  return None

steps = [0]

def follow_line(start, dir):
  steps[0] += 1
  pos = (start[0] + dir[0], start[1] + dir[1])
  last_viable = start
  dx, dy = dir
  while True:
    x, y = pos
    print(x, y)
    c = field[y][x]
    if c == '+':
      return pos
    if c == dch:
      last_viable = pos
    if c == 'U':
      print(steps)
      print(letters)
      die()
    if c in alpha:
      letters.append(c)
    steps[0] += 1
    pos = (x + dx, y + dy)

pos = start
while dir is not None:
  pos = follow_line(pos, dir)
  dir = dt(pos, dir)
print(steps)
print(letters)
